import React, { Component } from 'react';
import axios from 'axios';

class Customer extends Component {
  constructor(props) {
    super(props);
    this.state = {
      customers: [],
      imageName: '',
      imageFile: null,
      explanation: '',
      maskImageURL: '',
      inpaintedImageURL: '',
    };
  }

  componentDidMount() {
    this.getCustomers();
  }

  getCustomers() {
    axios
      .get('/api/main')
      .then((res) => {
        this.setState({ customers: res.data });
      })
      .catch((err) => console.log(err));
  }

  handleFileChange = (e) => {
    this.setState({
      imageFile: e.target.files[0],
      imageName: e.target.files[0].name.split('.')[0],
    });
  };

  handleTextChange = (e) => {
    this.setState({ explanation: e.target.value });
  };

  handleUpload = () => {
    const formData = new FormData();
    formData.append('image', this.state.imageFile);
    formData.append('name', this.state.imageName);
    formData.append('explanation', this.state.explanation);

    axios
      .post('/api/main', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      })
      .then((res) => {
        console.log(res);
        this.getCustomers();
        this.setState({
          imageName: '',
          imageFile: null,
          explanation: '',
        });
      })
      .catch((err) => console.log(err));
  };

  handleGenerateMask = () => {
    const canvas = document.getElementById('canvas');
    const maskImageDataURL = canvas.toDataURL('image/png');
    const imageName = this.state.imageName;

    axios
      .post('/api/saveImage', { imageDataURL: maskImageDataURL, imageName })
      .then((res) => {
        console.log(res);
        this.setState({ maskImageURL: res.data.maskImagePath });
        alert('마스크 이미지가 저장되었습니다.');
      })
      .catch((err) => console.log(err));
  };

  handleMergeImages = () => {
    axios
      .post('/api/mergeImages', {
        originalImage: this.state.customers[0].image,
        imageName: this.state.imageName,
      })
      .then((res) => {
        console.log(res);
        this.setState({ inpaintedImageURL: res.data.inpaintedImageURL });
        alert('이미지가 성공적으로 합쳐졌습니다.');
      })
      .catch((err) => console.log(err));
  };

  render() {
    const { customers } = this.state;

    return (
      <div>
        <h2>Customer</h2>
        <table border="1">
          <tbody>
            <tr>
              <th>Image</th>
              <th>Name</th>
              <th>Explanation</th>
            </tr>
            {customers.map((customer) => (
              <tr key={customer.id}>
                <td>
                  <img
                    src={customer.image}
                    alt={customer.name}
                    width="100"
                    height="100"
                  />
                </td>
                <td>{customer.name}</td>
                <td>{customer.explanation}</td>
                <td>
                  <button onClick={() => this.handleDelete(customer.id)}>
                    삭제
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
        <hr />
        <h3>이미지 업로드</h3>
        <div>
          <input type="file" onChange={this.handleFileChange} />
          <input
            type="text"
            placeholder="이름"
            value={this.state.imageName}
            onChange={this.handleTextChange}
          />
          <input
            type="text"
            placeholder="설명"
            value={this.state.explanation}
            onChange={this.handleTextChange}
          />
          <button onClick={this.handleUpload}>업로드</button>
        </div>
        <hr />
        <h3>마스크 생성</h3>
        <canvas id="canvas" width="400" height="300"></canvas>
        <button onClick={this.handleGenerateMask}>마스크 생성</button>
        <hr />
        <h3>이미지 합치기</h3>
        <button onClick={this.handleMergeImages}>이미지 합치기</button>
        <img
          src={this.state.inpaintedImageURL}
          alt="inpainted"
          width="100"
          height="100"
        />
      </div>
    );
  }
}

export default Customer;
