import React, { Component } from 'react';
import './Login.css';

class Signin extends Component {
  constructor(props) {
    super(props);
    this.state = {
      id: "",
      password: "",
      password2: ""
    };
  }

  handleChange = (event) => {
    const { name, value } = event.target;
    this.setState({ [name]: value });
  };

  handleSubmit = () => {
    const { id, password, password2 } = this.state;
    if (password !== password2) {
      alert("비밀번호가 일치하지 않습니다.");
      return;
    }

    const userData = {
      userId: id,
      userPassword: password,
      userPassword2: password2,
    };
    fetch("/signin", {
      method: "post",
      headers: {
        "content-type": "application/json",
      },
      body: JSON.stringify(userData),
    })
      .then((res) => res.json())
      .then((json) => {
        if(json.isSuccess === "True"){
          alert('회원가입이 완료되었습니다!')
          this.props.setMode("LOGIN");
        } else{
          alert(json.isSuccess);
        }
      });
  };

  render() {
    const { id, password, password2 } = this.state;
    return (
      <>
        <h2>회원가입</h2>

        <div className="form">
          <p>
            <input
              className="login"
              type="text"
              name="id"
              value={id}
              placeholder="아이디"
              onChange={this.handleChange}
            />
          </p>
          <p>
            <input
              className="login"
              type="password"
              name="password"
              value={password}
              placeholder="비밀번호"
              onChange={this.handleChange}
            />
          </p>
          <p>
            <input
              className="login"
              type="password"
              name="password2"
              value={password2}
              placeholder="비밀번호 확인"
              onChange={this.handleChange}
            />
          </p>

          <p>
            <input
              className="btn"
              type="submit"
              value="회원가입"
              onClick={this.handleSubmit}
            />
          </p>
        </div>

        <p>
          로그인화면으로 돌아가기  
          <button onClick={() => this.props.setMode("LOGIN")}>로그인</button>
        </p>
      </>
    ); 
  }
}

export default Signin;
