import React, { useState, useEffect } from 'react';
import './Login.css';

function Login(props) {
  const [id, setId] = useState("");
  const [password, setPassword] = useState("");

  return (
    <>
      <h2>로그인</h2>

      <div className="form">
        <p>
          <input
            className="login"
            type="text"
            name="username"
            placeholder="아이디"
            onChange={event => setId(event.target.value)}
          />
        </p>
        <p>
          <input
            className="login"
            type="password"
            name="pwd"
            placeholder="비밀번호"
            onChange={event => setPassword(event.target.value)}
          />
        </p>

        <p>
          <input
            className="btn"
            type="submit"
            value="로그인"
            onClick={() => {
              const userData = {
                userId: id,
                userPassword: password,
              };
              fetch("/login", {
                method: "post",
                headers: {
                  "content-type": "application/json",
                },
                body: JSON.stringify(userData),
              })
                .then((res) => res.json())
                .then((json) => {            
                  if(json.isLogin === "True"){
                    props.setMode("WELCOME");
                  } else {
                    alert(json.isLogin);
                  }
                });
            }}
          />
        </p>
      </div>

      <p>
        계정이 없으신가요?  
        <button onClick={() => props.setMode("SIGNIN")}>회원가입</button>
      </p>
    </>
  ); 
}

function Signin(props) {
  const [id, setId] = useState("");
  const [password, setPassword] = useState("");
  const [password2, setPassword2] = useState("");

  return (
    <>
      <h2>회원가입</h2>

      <div className="form">
        <p>
          <input
            className="login"
            type="text"
            placeholder="아이디"
            onChange={event => setId(event.target.value)}
          />
        </p>
        <p>
          <input
            className="login"
            type="password"
            placeholder="비밀번호"
            onChange={event => setPassword(event.target.value)}
          />
        </p>
        <p>
          <input
            className="login"
            type="password"
            placeholder="비밀번호 확인"
            onChange={event => setPassword2(event.target.value)}
          />
        </p>

        <p>
          <input
            className="btn"
            type="submit"
            value="회원가입"
            onClick={() => {
              const userData = {
                userId: id,
                userPassword: password,
                userPassword2: password2,
              };
              fetch("/signin", {
                method: "post",
                headers: {
                  "content-type": "application/json",
                },
                body: JSON.stringify(userData),
              })
                .then((res) => res.json())
                .then((json) => {
                  if(json.isSuccess === "True"){
                    alert('회원가입이 완료되었습니다!')
                    props.setMode("LOGIN");
                  } else{
                    alert(json.isSuccess);
                  }
                });
            }}
          />
        </p>
      </div>

      <p>
        로그인화면으로 돌아가기  
        <button onClick={() => props.setMode("LOGIN")}>로그인</button>
      </p>
    </>
  ); 
}

function LoginSignUp(props) {
  const [mode, setMode] = useState("");

  useEffect(() => {
    fetch("/authcheck")
      .then((res) => res.json())
      .then((json) => {        
        if (json.isLogin === "True") {
          setMode("WELCOME");
        } else {
          setMode("LOGIN");
        }
      });
  }, []); 

  let content = null;  

  if(mode === "LOGIN"){
    content = <Login setMode={setMode} /> 
  } else if (mode === 'SIGNIN') {
    content = <Signin setMode={setMode} /> 
  } else if (mode === 'WELCOME') {
    content = <>
      <h2>메인 페이지에 오신 것을 환영합니다</h2>
      <p>로그인에 성공하셨습니다.</p> 
      <a href="/logout">로그아웃</a>   
    </>
  }

  return (
    <>
      <div className="background">
        {content}
      </div>
    </>
  );
}

export default LoginSignUp;
