import React, { Component } from 'react';
import './Login.css';

class Login extends Component {
  constructor(props) {
    super(props);
    this.state = {
      id: "",
      password: ""
    };
  }

  handleChange = (event) => {
    const { name, value } = event.target;
    this.setState({ [name]: value });
  };

  handleSubmit = () => {
    const { id, password } = this.state;
    const userData = {
      userId: id,
      userPassword: password,
    };
    fetch("/login", {
      method: "post",
      headers: {
        "content-type": "application/json",
      },
      body: JSON.stringify(userData),
    })
      .then((res) => res.json())
      .then((json) => {            
        if(json.isLogin === "True"){
          this.props.setMode("WELCOME");
        } else {
          alert(json.isLogin);
        }
      });
  };

  render() {
    const { id, password } = this.state;
    return (
      <>
        <h2 className='form'>로그인</h2>

        <div className="form">
          <p>
            <input
              className="login"
              type="text"
              name="id"
              value={id}
              placeholder="아이디"
              onChange={this.handleChange}
            />
          </p>
          <p>
            <input
              className="login"
              type="password"
              name="password"
              value={password}
              placeholder="비밀번호"
              onChange={this.handleChange}
            />
          </p>

          <p>
            <input
              className="btn"
              type="submit"
              value="로그인"
              onClick={this.handleSubmit}
            />
          </p>
        </div>

        <p className='form'>
          계정이 없으신가요?  
          <button onClick={() => this.props.setMode("SIGNIN")}>회원가입</button>
        </p>
      </>
    ); 
  }
}

export default Login;
