import React, { Component } from 'react';
import CircularProgress from '@mui/material/CircularProgress';
import Customer from './Customer';
import DataAdd from './DataAdd';
import Box from '@mui/material/Box';
import AppBar from '@mui/material/AppBar';
import Toolbar from '@mui/material/Toolbar';
import Typography from '@mui/material/Typography';
import IconButton from '@mui/material/IconButton';
import MenuIcon from '@mui/icons-material/Menu';
import SearchIcon from '@mui/icons-material/Search';
import Paper from '@mui/material/Paper';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Button from '@mui/material/Button';
import { styled, alpha } from '@mui/material/styles';
import InputBase from '@mui/material/InputBase';
import './Login.css';

const Search = styled('div')(({ theme }) => ({
  position: 'relative',
  borderRadius: theme.shape.borderRadius,
  backgroundColor: alpha(theme.palette.common.white, 0.15),
  '&:hover': {
    backgroundColor: alpha(theme.palette.common.white, 0.25),
  },
  marginRight: theme.spacing(2),
  marginLeft: 0,
  width: '100%',
  [theme.breakpoints.up('sm')]: {
    marginLeft: theme.spacing(3),
    width: 'auto',
  },
}));

const SearchIconWrapper = styled('div')(({ theme }) => ({
  padding: theme.spacing(0, 2),
  height: '100%',
  position: 'absolute',
  pointerEvents: 'none',
  display: 'flex',
  alignItems: 'center',
  justifyContent: 'center',
}));

const StyledInputBase = styled(InputBase)(({ theme }) => ({
  color: 'inherit',
  '& .MuiInputBase-input': {
    padding: theme.spacing(1, 1, 1, 0),
    // vertical padding + font size from searchIcon
    paddingLeft: `calc(1em + ${theme.spacing(4)})`,
    transition: theme.transitions.create('width'),
    width: '100%',
    [theme.breakpoints.up('md')]: {
      width: '20ch',
    },
  },
}));

class Login extends Component {
  constructor(props) {
    super(props);
    this.state = {
      id: "",
      password: "",
    };
  }

  handleLogin = () => {
    const { id, password } = this.state;
    const userData = {
      userId: id,
      userPassword: password,
    };
    fetch("/login", {
      method: "post",
      headers: {
        "content-type": "application/json",
      },
      body: JSON.stringify(userData),
    })
    .then((res) => res.json())
    .then((json) => {            
      if(json.isLogin === "True"){
        this.props.setMode("WELCOME");
      } else {
        alert(json.isLogin);
      }
    });
  };

  render() {
    const { id, password } = this.state;
    return (
      <>
        <h2>로그인</h2>

        <div className="form">
          <p>
            <input
              className="login"
              type="text"
              name="username"
              placeholder="아이디"
              value={id}
              onChange={event => this.setState({ id: event.target.value })}
            />
          </p>
          <p>
            <input
              className="login"
              type="password"
              name="pwd"
              placeholder="비밀번호"
              value={password}
              onChange={event => this.setState({ password: event.target.value })}
            />
          </p>

          <p>
            <input
              className="btn"
              type="submit"
              value="로그인"
              onClick={this.handleLogin}
            />
          </p>
        </div>

        <p>
          계정이 없으신가요?  
          <button onClick={() => this.props.setMode("SIGNIN")}>회원가입</button>
        </p>
      </>
    ); 
  }
}

class Signin extends Component {
  constructor(props) {
    super(props);
    this.state = {
      id: "",
      password: "",
      password2: "",
    };
  }

  handleSignin = () => {
    const { id, password, password2 } = this.state;
    const userData = {
      userId: id,
      userPassword: password,
      userPassword2: password2,
    };
    fetch("/signin", {
      method: "post",
      headers: {
        "content-type": "application/json",
      },
      body: JSON.stringify(userData),
    })
    .then((res) => res.json())
    .then((json) => {
      if(json.isSuccess === "True"){
        alert('회원가입이 완료되었습니다!')
        this.props.setMode("LOGIN");
      } else{
        alert(json.isSuccess);
      }
    });
  };

  render() {
    const { id, password, password2 } = this.state;
    return (
      <>
        <h2>회원가입</h2>

        <div className="form">
          <p>
            <input
              className="login"
              type="text"
              placeholder="아이디"
              value={id}
              onChange={event => this.setState({ id: event.target.value })}
            />
          </p>
          <p>
            <input
              className="login"
              type="password"
              placeholder="비밀번호"
              value={password}
              onChange={event => this.setState({ password: event.target.value })}
            />
          </p>
          <p>
            <input
              className="login"
              type="password"
              placeholder="비밀번호 확인"
              value={password2}
              onChange={event => this.setState({ password2: event.target.value })}
            />
          </p>

          <p>
            <input
              className="btn"
              type="submit"
              value="회원가입"
              onClick={this.handleSignin}
            />
          </p>
        </div>

        <p>
          로그인화면으로 돌아가기  
          <button onClick={() => this.props.setMode("LOGIN")}>로그인</button>
        </p>
      </>
    ); 
  }
}

class LoginSignUp extends Component {
  constructor(props) {
    super(props);
    this.state = {
      mode: "",
      searchKeyword: "",
      imageData: null,
    };
  }

  componentDidMount() {
    fetch("/authcheck")
      .then((res) => res.json())
      .then((json) => {        
        if (json.isLogin === "True") {
          this.setState({ mode: "WELCOME" });
        } else {
          this.setState({ mode: "LOGIN" });
        }
      });

    fetch("/api/imageData")
      .then(res => res.json())
      .then(res => this.setState({ imageData: res }))
      .catch(err => console.error(err));
  }

  handleLogout = () => {
    fetch("/logout")
      .then((res) => res.json())
      .then((json) => {
        if (json.isSuccess === "True") {
          this.setState({ mode: "LOGIN" });
          document.cookie = "userLoggedIn=; expires=Thu, 01 Jan 1970 00:00:00 UTC; path=/;";
        } else {
          alert(json.isSuccess);
        }
      });
  };

  handleValueChange = (e) => {
    this.setState({ searchKeyword: e.target.value });
  };

  handleInpainting = () => {
    fetch("/inpainting")
      .then((res) => res.json())
      .then((json) => {
        if (json.isSuccess === "True") {
          alert('이미지 inpainting에 성공했습니다!');
          window.location.reload();
        } else {
          alert(json.isSuccess);
        }
      });
  };

  render() {
    const { mode, searchKeyword, imageData } = this.state;
    let content = null;  

    if(mode === "LOGIN"){
      content = <Login setMode={(mode) => this.setState({ mode })} /> 
    } else if (mode === 'SIGNIN') {
      content = <Signin setMode={(mode) => this.setState({ mode })} /> 
    } else if (mode === 'WELCOME') {
      content = (
        <div style={{ marginLeft: 10, marginRight: 10 }}>
          <Box sx={{ flexGrow: 1 }}>
            <AppBar position="static" sx={{ minWidth: '1080px' }}>
              <Toolbar>
                <IconButton
                  size="large"
                  edge="start"
                  color="inherit"
                  aria-label="open drawer"
                  sx={{ mr: 2 }}
                >
                  <MenuIcon />
                </IconButton>
                <Typography
                  variant="h6"
                  noWrap
                  component="div"
                  sx={{ flexGrow: 1, display: { xs: 'none', sm: 'block' } }}
                >
                  Image Inpainting WEB
                </Typography>
                <Search>
                  <SearchIconWrapper>
                    <SearchIcon />
                  </SearchIconWrapper>
                  <StyledInputBase
                    placeholder="Search"
                    inputProps={{ 'aria-label': 'search' }}
                    name="searchKeyword"
                    value={searchKeyword}
                    onChange={this.handleValueChange}
                  />
                </Search>
              </Toolbar>
            </AppBar>
          </Box><br />
          <DataAdd style={{ marginTop: 20, marginBottom: 20, display: 'flex', alignItems: "center", justifyContent: "center" }} stateRefresh={this.handleValueChange} />

          <br />

          <Paper sx={{ width: "100%" }}>
            <Table sx={{ minWidth: '1080px' }}>
              <TableHead>
                <TableRow>
                  {["Number", "Original Image", "Inpainted Image", "Name", "Explanation", "Setting"].map((c, index) => (
                    <TableCell key={index} sx={{ fontSize: '1.0rem' }}>{c}</TableCell>
                  ))}
                </TableRow>
              </TableHead>
              <TableBody>
                {imageData ? imageData
                  .filter(c => c.name.indexOf(searchKeyword) > -1)
                  .map(c => (
                    <Customer stateRefresh={this.handleValueChange} key={c.id} id={c.id} image={c.image} inpainted_image={c.inpainted_image} name={c.name} explanation={c.explanation} />
                  )) : <TableRow><TableCell colSpan="6" align="center"><CircularProgress /></TableCell></TableRow>}
              </TableBody>
            </Table>
          </Paper><br />
          <Button onClick={this.handleInpainting} variant="contained" color="primary" >Inpainting</Button>
          <Button onClick={this.handleLogout} style={{ marginLeft: 20 }} variant="contained" color="secondary" >Logout</Button>
        </div>
      );
    }

    return (
      <>
        <div className="background">
          {content}
        </div>
      </>
    );
  }
}

export default LoginSignUp;
