// SignUp.js
import React, { Component } from 'react';

class SignUp extends Component {
  constructor(props) {
    super(props);
    this.state = {
      id: '',
      password: '',
      name: '',
      school: '',
      department: '',
      purpose: ''
    };
  }

  handleChange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  }

  handleSubmit = (e) => {
    e.preventDefault();
    const { id, password, name, school, department, purpose } = this.state;
    fetch('/api/signup', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ id, password, name, school, department, purpose }),
    })
      .then(response => {
        if (response.ok) {
          return response.json();
        } else {
          throw new Error('회원가입에 실패했습니다.');
        }
      })
      .then(data => {
        alert(data.message);
        // 회원가입 성공 시 로그인 페이지로 이동하거나 자동으로 로그인하는 로직을 추가해야 합니다.
      })
      .catch(error => {
        console.error('회원가입 실패:', error);
        alert('회원가입에 실패했습니다.');
      });
  }

  render() {
    const { id, password, name, school, department, purpose } = this.state;
    return (
      <div>
        <h2>Sign Up</h2>
        <form onSubmit={this.handleSubmit}>
          <div>
            <label>ID: </label>
            <input type="text" name="id" value={id} onChange={this.handleChange} />
          </div>
          <div>
            <label>Password: </label>
            <input type="password" name="password" value={password} onChange={this.handleChange} />
          </div>
          <div>
            <label>Name: </label>
            <input type="text" name="name" value={name} onChange={this.handleChange} />
          </div>
          <div>
            <label>School: </label>
            <input type="text" name="school" value={school} onChange={this.handleChange} />
          </div>
          <div>
            <label>Department: </label>
            <input type="text" name="department" value={department} onChange={this.handleChange} />
          </div>
          <div>
            <label>Purpose: </label>
            <input type="text" name="purpose" value={purpose} onChange={this.handleChange} />
          </div>
          <button type="submit">Sign Up</button>
        </form>
      </div>
    );
  }
}

export default SignUp;
