import React, { Component } from 'react';
import LoginSignUp from './components/LoginSignUp';

import TableRow from '@mui/material/TableRow';
import TableCell from '@mui/material/TableCell';
import Table from '@mui/material/Table';
import TableHead from '@mui/material/TableHead';
import TableBody from '@mui/material/TableBody';
import CircularProgress from '@mui/material/CircularProgress'; 
import Paper from '@mui/material/Paper';

import { styled, alpha } from '@mui/material/styles';
import AppBar from '@mui/material/AppBar';
import Box from '@mui/material/Box';
import Toolbar from '@mui/material/Toolbar';
import IconButton from '@mui/material/IconButton';
import Typography from '@mui/material/Typography';
import InputBase from '@mui/material/InputBase';
import MenuIcon from '@mui/icons-material/Menu';
import SearchIcon from '@mui/icons-material/Search';
import Button from '@mui/material/Button'; 

import Customer from './components/Customer';
import DataAdd from './components/DataAdd';

const Search = styled('div')(({ theme }) => ({
  position: 'relative',
  borderRadius: theme.shape.borderRadius,
  backgroundColor: alpha(theme.palette.common.white, 0.15),
  '&:hover': {
    backgroundColor: alpha(theme.palette.common.white, 0.25),
  },
  marginLeft: 0,
  width: '100%',
  [theme.breakpoints.up('sm')]: {
    marginLeft: theme.spacing(1),
    width: 'auto',
  },
}));

const SearchIconWrapper = styled('div')(({ theme }) => ({
  padding: theme.spacing(0, 2),
  height: '100%',
  position: 'absolute',
  pointerEvents: 'none',
  display: 'flex',
  alignItems: 'center',
  justifyContent: 'center',
}));

const StyledInputBase = styled(InputBase)(({ theme }) => ({
  color: 'inherit',
  width: '100%',
  '& .MuiInputBase-input': {
    padding: theme.spacing(1, 1, 1, 0),
    paddingLeft: `calc(1em + ${theme.spacing(4)})`,
    transition: theme.transitions.create('width'),
    [theme.breakpoints.up('sm')]: {
      width: '12ch',
      '&:focus': {
        width: '20ch',
      },
    },
  },
}));

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      mode: '',
      imageData: null,
      completed: 0,
      searchKeyword: ''
    };
  }

  stateRefresh = () => {
    this.setState({
      imageData: '',
      completed: 0,
      searchKeyword: ''
    });
    this.callApi()
      .then(res => this.setState({ imageData: res }))
      .catch(err => console.log(err));
  }

  componentDidMount() {
    this.timer = setInterval(this.progress, 800); 
    this.callApi()
      .then(res => this.setState({ imageData: res }))
      .catch(err => console.log(err));
  }

  callApi = async () => {
    const response = await fetch('/api/main');
    const body = await response.json();
    return body;
  }

  progress = () => { 
    const { completed } = this.state;
    this.setState({ completed: completed >= 100 ? 0 : completed + 10 });
  }

  handleValueChange = (e) => {
    let nextState = {};
    nextState[e.target.name] = e.target.value;
    this.setState(nextState);
  }

  handleInpainting = () => {
    this.setState({ loading: true, completed: 0 });

    fetch('/api/runInpaintingScript')
      .then(response => {
        if (response.ok) {
          alert('Inpainting 작업이 성공적으로 실행되었습니다.');
          this.stateRefresh();
        } else {
          throw new Error('Inpainting 작업 실행에 실패했습니다.');
        }
      })
      .catch(error => {
        console.error('Inpainting 작업 실패:', error);
      })
      .finally(() => {
        this.setState({ loading: false });
      });
  }

  handleModeChange = (mode) => {
    this.setState({ mode });
  }

  handleLogout = () => {
    fetch("/logout")
      .then((res) => res.json())
      .then((json) => {
        if (json.isSuccess === "True") {
          this.setState({ mode: "LOGIN" });
          document.cookie = "userLoggedIn=; expires=Thu, 01 Jan 1970 00:00:00 UTC; path=/;";
        } else {
          alert(json.isSuccess);
        }
      })
      .catch((error) => {
        console.error("로그아웃 오류:", error);
        this.setState({ mode: "LOGIN" });
        document.cookie = "userLoggedIn=; expires=Thu, 01 Jan 1970 00:00:00 UTC; path=/;";
      });
  };

  render() {
    const { mode, imageData, completed, searchKeyword } = this.state;

    if (mode === 'WELCOME') {
      return (
        <div style={{ marginLeft: 10, marginRight: 10 }}>
          <Box sx={{ flexGrow: 1 }}>
            <AppBar position="static" sx={{ minWidth: '1080px' }}>
              <Toolbar>
                <IconButton
                  size="large"
                  edge="start"
                  color="inherit"
                  aria-label="open drawer"
                  sx={{ mr: 2 }}
                >
                  <MenuIcon />
                </IconButton>
                <Typography
                  variant="h6"
                  noWrap
                  component="div"
                  sx={{ flexGrow: 1, display: { xs: 'none', sm: 'block' } }}
                >
                  Image Inpainting WEB
                </Typography>
                <Search>
                  <SearchIconWrapper>
                    <SearchIcon />
                  </SearchIconWrapper>
                  <StyledInputBase
                    placeholder="Search"
                    inputProps={{ 'aria-label': 'search' }}
                    name="searchKeyword"
                    value={searchKeyword}
                    onChange={this.handleValueChange}
                  />
                </Search>
              </Toolbar>
            </AppBar>
          </Box><br />
          <DataAdd style={{ marginTop: 20, marginBottom: 20, display: 'flex', alignItems: "center", justifyContent: "center" }} stateRefresh={this.stateRefresh} />

          <br />

          <Paper sx={{ width: "100%" }}>
            <Table sx={{ minWidth: '1080px' }}>
              <TableHead>
                <TableRow>
                  {["Number", "Original Image", "Inpainted Image", "Name", "Explanation", "Setting"].map((c, index) => (
                    <TableCell key={index} sx={{ fontSize: '1.0rem' }}>{c}</TableCell>
                  ))}
                </TableRow>
              </TableHead>
              <TableBody>
                {imageData ? imageData
                  .filter(c => c.name.indexOf(searchKeyword) > -1)
                  .map(c => (
                    <Customer stateRefresh={this.stateRefresh} key={c.id} id={c.id} image={c.image} inpainted_image={c.inpainted_image} name={c.name} explanation={c.explanation} />
                  )) : <TableRow><TableCell colSpan="6" align="center"><CircularProgress /></TableCell></TableRow>}
              </TableBody>
            </Table>
          </Paper><br />
          <Button onClick={this.handleInpainting} variant="contained" color="primary" >Inpainting</Button>
          <Button onClick={this.handleLogout} style={{ marginLeft: 20 }} variant="contained" color="secondary" >Logout</Button>
        </div>
      );
    } else {
      return (
        <div style={{ marginLeft: 10, marginRight: 10 }}>
          <LoginSignUp setMode={this.handleModeChange} />
        </div>
      );
    }
  }
}

export default App;
